import { create } from "zustand";

interface AudioState {
  backgroundMusic: HTMLAudioElement | null;
  hitSound: HTMLAudioElement | null;
  successSound: HTMLAudioElement | null;
  isMuted: boolean;

  // Setter functions
  setBackgroundMusic: (music: HTMLAudioElement) => void;
  setHitSound: (sound: HTMLAudioElement) => void;
  setSuccessSound: (sound: HTMLAudioElement) => void;

  // Control functions
  toggleMute: () => void;
  playHit: () => void;
  playSuccess: () => void;
}

export const useAudio = create<AudioState>((set, get) => ({
  backgroundMusic: null,
  hitSound: null,
  successSound: null,
  isMuted: true, // Start muted by default

  setBackgroundMusic: (music) => set({ backgroundMusic: music }),
  setHitSound: (sound) => set({ hitSound: sound }),
  setSuccessSound: (sound) => set({ successSound: sound }),

  toggleMute: () => {
    const { isMuted } = get();
    const newMutedState = !isMuted;

    // Just update the muted state
    set({ isMuted: newMutedState });

    // Log the change
    console.log(`Sound ${newMutedState ? 'muted' : 'unmuted'}`);
  },

  playHit: () => {
    const { hitSound, isMuted } = get();
    if (hitSound) {
      // If sound is muted, don't play anything
      if (isMuted) {
        console.log("Hit sound skipped (muted)");
        return;
      }

      // Clone the sound to allow overlapping playback
      const soundClone = hitSound.cloneNode() as HTMLAudioElement;
      soundClone.volume = 0.3;
      soundClone.play().catch(error => {
        console.log("Hit sound play prevented:", error);
      });
    }
  },

  playSuccess: () => {
    const { successSound, isMuted } = get();
    if (successSound) {
      // If sound is muted, don't play anything
      if (isMuted) {
        console.log("Success sound skipped (muted)");
        return;
      }

      successSound.currentTime = 0;
      successSound.play().catch(error => {
        console.log("Success sound play prevented:", error);
      });
    }
  }
}));

client/src/lib/stores/useGame.tsx.content:
import { create } from "zustand";
import { subscribeWithSelector } from "zustand/middleware";

export type GamePhase = "ready" | "playing" | "ended";

interface GameState {
  phase: GamePhase;

  // Actions
  start: () => void;
  restart: () => void;
  end: () => void;
}

export const useGame = create<GameState>()(
  subscribeWithSelector((set) => ({
    phase: "ready",

    start: () => {
      set((state) => {
        // Only transition from ready to playing
        if (state.phase === "ready") {
          return { phase: "playing" };
        }
        return {};
      });
    },

    restart: () => {
      set(() => ({ phase: "ready" }));
    },

    end: () => {
      set((state) => {
        // Only transition from playing to ended
        if (state.phase === "playing") {
          return { phase: "ended" };
        }
        return {};
      });
    }
  }))
);

client/src/lib/stores/useGameStore.tsx.content:
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

export type GameScreen = 'menu' | 'stage-select' | 'game';
export type GameState = 'playing' | 'paused' | 'ended' | 'stage-complete';

interface GameStore {
  currentScreen: GameScreen;
  currentStage: number;
  gameState: GameState;
  unlockedStages: number;

  // Actions
  setCurrentScreen: (screen: GameScreen) => void;
  setCurrentStage: (stage: number) => void;
  setGameState: (state: GameState) => void;
  unlockNextStage: () => void;
  goToNextStage: () => void;
  resetProgress: () => void;
}

export const useGameStore = create<GameStore>()(
  persist(
    (set, get) => ({
      currentScreen: 'menu',
      currentStage: 1,
      gameState: 'playing',
      unlockedStages: 1,

      setCurrentScreen: (screen) => set({ currentScreen: screen }),

      setCurrentStage: (stage) => set({ 
        currentStage: stage,
        gameState: 'playing'
      }),

      setGameState: (state) => set({ gameState: state }),

      unlockNextStage: () => set((state) => ({
        unlockedStages: Math.max(state.unlockedStages, state.currentStage + 1)
      })),

      goToNextStage: () => {
        const { currentStage, unlockedStages } = get();
        if (currentStage < 5 && currentStage < unlockedStages) {
          set({ 
            currentStage: currentStage + 1,
            gameState: 'playing',
            currentScreen: 'game'
          });
        } else {
          set({ currentScreen: 'stage-select' });
        }
      },

      resetProgress: () => set({
        unlockedStages: 1,
        currentStage: 1,
        gameState: 'playing',
        currentScreen: 'menu'
      })
    }),
    {
      name: 'cultural-arcade-game-store',
      partialize: (state) => ({
        unlockedStages: state.unlockedStages
      })
    }
  )
);

client/src/lib/stores/useScoreStore.tsx.content:
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface ScoreStore {
  scores: Record<number, number>;
  highScores: Record<number, number>;

  // Actions
  updateScore: (stage: number, score: number) => void;
  resetScores: () => void;
  getHighScore: (stage: number) => number;
}

export const useScoreStore = create<ScoreStore>()(
  persist(
    (set, get) => ({
      scores: {},
      highScores: {},

      updateScore: (stage, score) => {
        set((state) => {
          const newScores = { ...state.scores, [stage]: score };
          const newHighScores = { ...state.highScores };

          if (!newHighScores[stage] || score > newHighScores[stage]) {
            newHighScores[stage] = score;
          }

          return {
            scores: newScores,
            highScores: newHighScores
          };
        });
      },

      resetScores: () => set({
        scores: {},
        highScores: {}
      }),

      getHighScore: (stage) => {
        return get().highScores[stage] || 0;
      }
    }),
    {
      name: 'cultural-arcade-scores',
      partialize: (state) => ({
        highScores: state.highScores
      })
    }
  )
);

