export class GameRenderer {
  private ctx: CanvasRenderingContext2D;

  constructor(ctx: CanvasRenderingContext2D) {
    this.ctx = ctx;
  }

  drawSprite(x: number, y: number, width: number, height: number, color: string) {
    this.ctx.save();
    this.ctx.fillStyle = color;
    this.ctx.fillRect(x - width / 2, y - height / 2, width, height);
    this.ctx.restore();
  }

  drawCircle(x: number, y: number, radius: number, color: string, stroke = false) {
    this.ctx.save();
    this.ctx.beginPath();
    this.ctx.arc(x, y, radius, 0, Math.PI * 2);

    if (stroke) {
      this.ctx.strokeStyle = color;
      this.ctx.stroke();
    } else {
      this.ctx.fillStyle = color;
      this.ctx.fill();
    }

    this.ctx.restore();
  }

  drawText(text: string, x: number, y: number, size = 16, color = 'white', align: CanvasTextAlign = 'left') {
    this.ctx.save();
    this.ctx.font = `${size}px Inter, sans-serif`;
    this.ctx.fillStyle = color;
    this.ctx.textAlign = align;
    this.ctx.fillText(text, x, y);
    this.ctx.restore();
  }

  clear(width: number, height: number) {
    this.ctx.clearRect(0, 0, width, height);
  }

  drawGradient(x: number, y: number, width: number, height: number, colors: string[]) {
    const gradient = this.ctx.createLinearGradient(x, y, x, y + height);
    colors.forEach((color, index) => {
      gradient.addColorStop(index / (colors.length - 1), color);
    });

    this.ctx.save();
    this.ctx.fillStyle = gradient;
    this.ctx.fillRect(x, y, width, height);
    this.ctx.restore();
  }
}

client/src/lib/utils/InputHandler.ts.content:
export class InputHandler {
  private keys: Set<string> = new Set();
  private callbacks: Map<string, Function[]> = new Map();

  constructor() {
    this.setupEventListeners();
  }

  private setupEventListeners() {
    document.addEventListener('keydown', (e) => {
      this.keys.add(e.code);
      this.triggerCallbacks('keydown', e.code);
    });

    document.addEventListener('keyup', (e) => {
      this.keys.delete(e.code);
      this.triggerCallbacks('keyup', e.code);
    });
  }

  isKeyPressed(key: string): boolean {
    return this.keys.has(key);
  }

  onKeyDown(key: string, callback: Function) {
    const eventKey = `keydown:${key}`;
    if (!this.callbacks.has(eventKey)) {
      this.callbacks.set(eventKey, []);
    }
    this.callbacks.get(eventKey)!.push(callback);
  }

  onKeyUp(key: string, callback: Function) {
    const eventKey = `keyup:${key}`;
    if (!this.callbacks.has(eventKey)) {
      this.callbacks.set(eventKey, []);
    }
    this.callbacks.get(eventKey)!.push(callback);
  }

  private triggerCallbacks(event: string, key: string) {
    const eventKey = `${event}:${key}`;
    const callbacks = this.callbacks.get(eventKey);
    if (callbacks) {
      callbacks.forEach(callback => callback());
    }
  }

  destroy() {
    this.callbacks.clear();
    this.keys.clear();
  }
}

client/src/lib/utils/ParticleSystem.ts.content:
interface Particle {
  x: number;
  y: number;
  vx: number;
  vy: number;
  life: number;
  maxLife: number;
  size: number;
  color: string;
  gravity?: number;
}

export class ParticleSystem {
  private particles: Particle[] = [];
  private ctx: CanvasRenderingContext2D;

  constructor(ctx: CanvasRenderingContext2D) {
    this.ctx = ctx;
  }

  addExplosion(x: number, y: number, count = 20, color = '#FFD700') {
    for (let i = 0; i < count; i++) {
      const angle = (Math.PI * 2 * i) / count;
      const speed = 2 + Math.random() * 4;

      this.particles.push({
        x,
        y,
        vx: Math.cos(angle) * speed,
        vy: Math.sin(angle) * speed,
        life: 60,
        maxLife: 60,
        size: 2 + Math.random() * 3,
        color,
        gravity: 0.1
      });
    }
  }

  addTrail(x: number, y: number, vx: number, vy: number, color = '#00FFFF') {
    this.particles.push({
      x,
      y,
      vx: vx * 0.1 + (Math.random() - 0.5) * 0.5,
      vy: vy * 0.1 + (Math.random() - 0.5) * 0.5,
      life: 30,
      maxLife: 30,
      size: 1 + Math.random() * 2,
      color
    });
  }

  update() {
    this.particles = this.particles.filter(particle => {
      // Update position
      particle.x += particle.vx;
      particle.y += particle.vy;

      // Apply gravity if specified
      if (particle.gravity) {
        particle.vy += particle.gravity;
      }

      // Update life
      particle.life--;

      return particle.life > 0;
    });
  }

  render() {
    this.particles.forEach(particle => {
      const alpha = particle.life / particle.maxLife;

      this.ctx.save();
      this.ctx.globalAlpha = alpha;
      this.ctx.fillStyle = particle.color;
      this.ctx.beginPath();
      this.ctx.arc(particle.x, particle.y, particle.size * alpha, 0, Math.PI * 2);
      this.ctx.fill();
      this.ctx.restore();
    });
  }

  clear() {
    this.particles = [];
  }
}

client/src/lib/utils/Physics.ts.content:
export interface Vector2 {
  x: number;
  y: number;
}

export class PhysicsUtils {
  static distance(a: Vector2, b: Vector2): number {
    const dx = a.x - b.x;
    const dy = a.y - b.y;
    return Math.sqrt(dx * dx + dy * dy);
  }

  static normalize(vector: Vector2): Vector2 {
    const length = Math.sqrt(vector.x * vector.x + vector.y * vector.y);
    if (length === 0) return { x: 0, y: 0 };
    return { x: vector.x / length, y: vector.y / length };
  }

  static multiply(vector: Vector2, scalar: number): Vector2 {
    return { x: vector.x * scalar, y: vector.y * scalar };
  }

  static add(a: Vector2, b: Vector2): Vector2 {
    return { x: a.x + b.x, y: a.y + b.y };
  }

  static subtract(a: Vector2, b: Vector2): Vector2 {
    return { x: a.x - b.x, y: a.y - b.y };
  }

  static circleCollision(pos1: Vector2, radius1: number, pos2: Vector2, radius2: number): boolean {
    const distance = this.distance(pos1, pos2);
    return distance < (radius1 + radius2);
  }

  static rectCollision(pos1: Vector2, size1: Vector2, pos2: Vector2, size2: Vector2): boolean {
    return pos1.x < pos2.x + size2.x &&
           pos1.x + size1.x > pos2.x &&
           pos1.y < pos2.y + size2.y &&
           pos1.y + size1.y > pos2.y;
  }

  static wrapPosition(position: Vector2, bounds: Vector2): Vector2 {
    return {
      x: ((position.x % bounds.x) + bounds.x) % bounds.x,
      y: ((position.y % bounds.y) + bounds.y) % bounds.y
    };
  }

  static clampPosition(position: Vector2, min: Vector2, max: Vector2): Vector2 {
    return {
      x: Math.max(min.x, Math.min(max.x, position.x)),
      y: Math.max(min.y, Math.min(max.y, position.y))
    };
  }
}
